import React, { Component } from 'react';
import './App.css';
import Web3 from 'web3'


class App extends Component {
  // data = '';


  componentWillMount() {
    this.loadBlockchainData(null)
  }

  async loadBlockchainData(event) {
    let url;
    if (!event) {
       url = 'http://localhost:7545'
    }
    else {
       url = event.target.value
    }
    const web3 = new Web3(url)
    const network = await web3.eth.net.getNetworkType()
    const ProjectId = '723c0eab8d2e4f30a10fc4a2ce2a08f9'
    const networks = [
      'https://mainnet.infura.io/v3/'+ProjectId,
      'https://ropsten.infura.io/v3/'+ProjectId,
      'https://rinkeby.infura.io/v3/'+ProjectId,
      'https://kovan.infura.io/v3/'+ProjectId,
      'https://goerli.infura.io/v3/'+ProjectId,
      'http://localhost:7545'
    ]
    const accounts = await web3.eth.getAccounts()
    //console.log('accounts:', accounts)

    this.setState({networks: networks.map((number) =>
      <option>{number}</option>)})

    this.setState({accounts: accounts.map((number) =>
    [<input type="radio" name="accounts" value={number} id={number} />,<label for={number}> {number}</label>, <br></br>])})

    console.log('network:', network)

    //onChange={this.loadBlockchainData}

  }

  constructor(props) {
    super(props)
    this.state = { accounts: '' , networks: ''}

  }

  render() {
    return (
      <div className="container">
        <h1>BEST WALLET</h1>
        <p>Choose network:  <select onChange={this.loadBlockchainData}>{this.state.networks}</select></p>
        <p>Your accounts: <br></br>{this.state.accounts}</p>

      </div>


    );
  }

   checkNetwork(event){
     this.data = event.target.value;
     console.log(this.data)
   }

   checkAccounts(event) {

   }


}

export default App;
