import React, { Component } from 'react';
import './App.css';
import Web3 from 'web3'


class App extends Component {
  // data = '';


  componentWillMount() {
    const ProjectId = '723c0eab8d2e4f30a10fc4a2ce2a08f9'
    const networks = [
      'http://localhost:7545',
      'https://mainnet.infura.io/v3/'+ProjectId,
      'https://ropsten.infura.io/v3/'+ProjectId,
      'https://rinkeby.infura.io/v3/'+ProjectId,
      'https://kovan.infura.io/v3/'+ProjectId,
      'https://goerli.infura.io/v3/'+ProjectId
    ]
    this.setState({networks: networks.map((number) =>
      <option>{number}</option>)})
    this.loadBlockchainData(null)
  }

  async loadBlockchainData(event) {
    let url;
    if (!event) {
       url = 'http://localhost:7545'
    }
    else {
       url = event.target.value
    }

    const web3 = new Web3(url)
    //console.log(web3)
    this.setState({web3})
    //console.log(url)

    //const network = await web3.eth.net.getNetworkType()

    const accounts = await web3.eth.getAccounts()
    console.log('accounts', this.state.accounts)
    console.log('users', this.state.users)
    if (accounts.length > 0) {
      const Addr = accounts[0]
      let bal;
      web3.eth.getBalance(Addr, (err, wei) => {
        bal = web3.utils.fromWei(wei, 'ether');
        this.setState({balance: bal})
      })
      //console.log(Addr)
    }
    else {
      this.setState({balance: ""})
    }

    //console.log('accounts:', accounts)
    //console.log('network:', network)
    var accounts1 = accounts.map((number) => <option>{number}</option>)
    var users = this.state.users.map((number) => <option>{number.address}</option>)
    console.log('users2', users)
    accounts1 = [...users, ...accounts1]
    this.setState({accounts: accounts1})

    //[<input type="radio" onChange={this.changeAddr} name="accounts" value={number} id={number} />,<label for={number}> {number}</label>, <br></br>])})

    //console.log(this.state)

    //onChange={this.loadBlockchainData}

  }

  constructor(props) {
    super(props)
    this.state = { accounts: '' , networks: '', balance: '', web3: Web3, users: [], privkey: ''}
  }

  render() {
    return (
      <div className="container">
        <h1>BEST WALLET</h1>
        <p>Choose network:  <select onChange={this.loadBlockchainData.bind(this)}>{this.state.networks}</select></p>
        <p>Choose Account: <select onChange={this.changeAddr.bind(this)}>{this.state.accounts}</select></p>
        <p>Balance: {this.state.balance}</p>
        <input type="submit" value='Cоздать новый адресс' onClick={this.newAddr.bind(this)} />
          <p><label> Приватный ключ <input type="password" name="privkey" value={this.state.privkey}
                            onChange={this.onPasswordChange}/></label><input type="submit" value="Импорт" onClick={this.importKey.bind(this)}/></p>

      </div>


    );
  }
  onPasswordChange(event){
    this.setState({privkey: event.target.value});
  }

  importKey(event) {
    console.log('privkey',this.state.privkey)
  }

   checkNetwork(event){
     //this.state = { accounts: '' , networks: ''}
     console.log(this.data)
   }

   changeAddr(event) {
    const Addr = event.target.value
    //console.log(this.state.web3)
    const web3 = this.state.web3
    //console.log(web3) 
    let bal;
    web3.eth.getBalance(Addr, (err, wei) => {
      bal = web3.utils.fromWei(wei, 'ether');
      this.setState({balance: bal})
    })

   }

   newAddr(event) {
    //console.log(event.target.value)
    //const accounts = this.state.accounts
    const web3 = this.state.web3
    var account = web3.eth.accounts.create()
    //accounts.append(account)
    console.log(account)
    var users = [account, ...this.state.users]
    var accounts = [<option>{account.address}</option>, ...this.state.accounts]
    users.map((user) => {accounts = [<option>{user.address}</option>, ...this.state.accounts]})
    this.setState({users: users})
    this.setState({accounts: accounts})
    let bal;
      web3.eth.getBalance(account.address, (err, wei) => {
        bal = web3.utils.fromWei(wei, 'ether');
        this.setState({balance: bal})
      })
    console.log('accounts', this.state.accounts)
    console.log('users', this.state.users)
   }


}

export default App;
