import './App.css';
import Web3 from 'web3'

class App extends Component {

  async loadBlockchainData() {
    const web3 = new Web3(Web3.givenProvider || 'http://localhost:8545')
  }

  render() {
    return (
      <div className="container">
        <h1>Hello, World!</h1>
      </div>
    );
  }
}

export default App;
