import React, { Component } from 'react';
import './App.css';
import Web3 from 'web3'

var SearchResult = React.createClass({
  getInitialState: function () {
    return {
      site: '',
      address: ''
    };
  },
  onSiteChanged: function (e) {
    this.setState({
      site: e.currentTarget.value
      });
  },

  onAddressChanged: function (e) {
    this.setState({
      address: e.currentTarget.value
      });
  },

  render: function(){
       var resultRows = this.props.data.map(function(result){
           return (
               <tbody>
                    <tr>
                        <td><input type="radio" name="site_name" 
                                   value={result.SITE_NAME} 
                                   checked={this.state.site === result.SITE_NAME} 
                                   onChange={this.onSiteChanged} />{result.SITE_NAME}</td>
                        <td><input type="radio" name="address" 
                                   value={result.ADDRESS}  
                                   checked={this.state.address === result.ADDRESS} 
                                   onChange={this.onAddressChanged} />{result.ADDRESS}</td>
                    </tr>
               </tbody>
           );
       }, this);
       return (
           <table className="table">
               <thead>
                   <tr>
                       <th>Name</th>
                       <th>Address</th>
                   </tr>
               </thead>
                {resultRows}
               <tfoot>
                   <tr>
                       <td>chosen site name {this.state.site} </td>
                       <td>chosen address {this.state.address} </td>
                   </tr>
               </tfoot>
           </table>
       );
  }
});

class App extends Component {

  componentWillMount() {
    this.loadBlockchainData( )
  }

  async loadBlockchainData() {
    const web3 = new Web3('http://localhost:7545')
    const network = await web3.eth.net.getNetworkType()
    const accounts = await web3.eth.getAccounts()
    //console.log('accounts:', accounts)
    this.setState({accounts: accounts.map((number) =>
    [<input type="radio" name="chooseone" value={number} />,<label for={number}> {number}</label>, <br></br>])})

    console.log('network:', network)
  }

  constructor(props) {
    super(props)
    this.state = { account: '' }
  }

  render() {
    return (
      <div className="container">
        <h1>Hello, World!</h1>
        <p>Your accounts: <br></br>{this.state.accounts}</p>

      </div>
    );
  }




}

export default App;
